mapper.select:
SELECT SEQ, TITLE, CONTENT, WRITER, PARENT_NO, CHILD_NO, GROUP_NO, NOTICE_YN FROM QNA_BOARD
WHERE TITLE LIKE '%${searchValue}%'
ORDER BY NOTICE_YN DESC, PARENT_NO DESC, CHILD_NO ASC LIMIT #{offset}, #{dataPerPage}

SELECT SEQ, TITLE, CONTENT, WRITER, PARENT_NO, CHILD_NO, GROUP_NO
FROM QNA_BOARD
WHERE SEQ = #{seq}

mapper.update:
UPDATE QNA_BOARD
SET
    TITLE = #{TITLE}
    ,CONTENT = #{CONTENT}
    ,WRITER = #{WRITER}
WHERE SEQ = #{SEQ}
